print "Let's start\n";
my $n = 1;
my $Tn = Tn($n);
my $val = $Tn;
my $maxDivs = 0;
my $maxN = 0;
for (10000.. 15000)   {
	$val = Tn($_);
	
	my $numDivs = numFactors($val);
	if ( $numDivs > $maxDivs ) {
		$maxDivs = $numDivs;
		$maxN = $n;
		print "$val (Tn=$_): ";
		my @a =  primeDivisors($val);
		print join(" " ,@a);			
		print  " (". $numDivs .")\n";;
	}
}

sub Tn { 
	my $n = shift;
	return ($n*$n + $n)/2;
}

sub primeDivisors {
	my $num = shift;
	my $n = $num;
	my %primeFactors;
	# print "$num: ";
	my $s = sqrt($num);
	
	LOOP: for ( my $i=2; $i < $n; $i++ ) {
		if ( isPrime($i) ) {
			while ($num % $i == 0) {
				$primeFactors{$i}++;
				$num/=$i;				
				if ( $num == 1 ) {
					last LOOP;
				}			
			}
		}
	}
	
	#print "\n".Dumper(\%primeFactors);
	#if ( keys %primeFactors > 0 ) {
		for my $k (keys %primeFactors) { 
			print "$k**$primeFactors{$k} "; 
		}
	#}
	return keys(%primeFactors);
}


