			if ( frac(2/($pow)) > frac($n/$pow) )
			 {}
		}	
	}
}

sub frac {
	my $n = shift;
	return $n - floor($n);
}


sub isPrime  {
	my $num = shift;	
	if ( $num == 0 ) { return 0; }
	if ( $num == 1 ) { return 0; }
	for (2 .. sqrt($num)) {
		if ( $num % $_  == 0 ){
			return 0;
		}
	}
	return 1;
}

sub factors {
	my $n = shift;
	my %factors;
	#$count =1 ;
	print "$n: 1 ";
	if ( $n % 2 == 0 ) {
		$factors{2}=1;
		$factors{$n/2}=1;
	}
	for ($i=3; $i <= $n/2; $i+=2 ) { 
		
		if ( $n % $i == 0 ) {
			$div = $n/$i;
			$factors{$div}=1;
			$factors{$i}=1;
	 	}
	}
	
	$factors{$n}=1;
	$factors{1}=1;
	# return sort {$a <=> $b} @factors;
	#return keys %factors;
	return sort {$a <=> $b} keys %factors;
}

sub numFactors {
	my $num = shift;
	my $n = $num;
	my %primeFactors;

	my $s = sqrt($num);
	
	
	# First let's check the primes we know (	@primeA
	my $pi=0;
	#print "$num: ";	
	#print "Looking through primes array\n";
	PRIME: for ($pi=0; $pi < length(@primeA); $pi++ ) {
		my $prime = $primeA[$pi];
		#print "Checking prime $prime\n";
		if ($prime > $s ) { last; }
		while ($num % $prime == 0) {
			$primeFactors{$prime}++;
			$num/=$prime;				
			if ( $num == 1 ) {
				last PRIME;
			}
		}
	}
	#print "We're left at $primeA[$pi]	\n";
	if ( $num != 1 ) {
		#print "Using old fashioned method\n";
		LOOP: for ( my $i=$primeA[$pi]; $i < sqrt($n); $i+=2 ) {
			
			if ( isPrime($i) ) {
				while ($num % $i == 0) {
					$primeFactors{$i}++;
					$num/=$i;				
					if ( $num == 1 ) {
						last LOOP;
					}			
				}
			}
		}
	}
	
	#print "\n".Dumper(\%primeFactors);
	#if ( keys %primeFactors > 0 ) {
	my $numDivs = 1;
		for my $v (values %primeFactors) { 
			$numDivs *= $v+1;
		}
	#}
	return $numDivs;
}